    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
     String Book_id=jTextField1.getText();
     String Book_title=jTextField9.getText();
     String Edition=jTextField3.getText();
     String Author=jTextField4.getText();
     String Published_by=jTextField5.getText();
     String Genre=jTextField6.getText(); 
     String Price_in_Rs=jTextField7.getText();
     String Total_Pages=jTextField8.getText();
     try{
         Class.forName("com.mysql.jdbc.Driver");  
        Connection con=DriverManager.getConnection(  
        "jdbc:mysql://localhost:3306/book_management_db","root","Jenish@3116");
         Statement st=con.createStatement();
         st.executeUpdate("insert into books_info values('"+Book_id+"','"+Book_title+"','"+ Edition+"','"+Author+"','"+Published_by+"','"+Genre+"','"+Price_in_Rs+"','"+Total_Pages+"')");
         JOptionPane.showMessageDialog(null,"Updated"); 
         setVisible(false);
            new newBook().setVisible(true);
     }
     catch(Exception e)
     {
       JOptionPane.showMessageDialog(null,"Something is Wrong!!"); 
         setVisible(false);
            new newBook().setVisible(true);
            }
        
    }//GEN-LAST:event_jButton1ActionPerformed

